mapper.select:
SELECT * FROM RAIN_cSpot_tbl cs INNER JOIN RAIN_cCompany_tbl cc ON cc.companyNo = cs.companyNo INNER JOIN RAIN_cUser_tbl cu ON cs.spotNo = cu.spotNo WHERE cs.spStatus IN
#{item}
AND ( cc.comName LIKE CONCAT('%', #{keyword}, '%') OR cs.spName LIKE CONCAT('%', #{keyword}, '%') OR cs.spAddr LIKE CONCAT('%', #{keyword}, '%') OR cu.userName LIKE CONCAT('%', #{keyword}, '%') )

mapper.update:
UPDATE RAIN_cSpot_tbl 
SET 
	companyNo = #{companyNo},
    spName = #{spName},
    spAddr = #{spAddr},
    spDetailAddr = #{spDetailAddr},
    spContact = #{spContact},
    spAgreementFile = #{filePath},
    spAgreementTerm = #{spAgreementTerm},
    spAutoExtension = #{spAutoExtension},
    spPayMethod = #{spPayMethod},
    spEmpNum = #{spEmpNum},
    spBdgt = #{spBdgt},
    spStatus = #{spStatus}


        , spChangeDate = #{spChangeDate}



        , spCancelEname = #{spCancelEname}



        , spCancelReason = #{spCancelReason}



        , spCancelDetailReason = #{spCancelDetailReason}

WHERE 
    spotNo = #{spotNo}
